mapper.insert:
INSERT INTO
tb_curriculum_vitae(
user_id, file_name, address, create_time, last_edit_time
) VALUES (
#{userId},#{fileName},#{address},#{createTime},#{lastEditTime}
)

mapper.select:
SELECT curriculum_vitae_id, user_id, file_name, address, create_time, last_edit_time FROM tb_curriculum_vitae
AND user_id=#{userId}
AND curriculum_vitae_id=#{curriculumVitaeId}
ORDER BY last_edit_time DESC
